          <div className="flex flex-wrap justify-center gap-4 mb-12">
            {['All', 'Web', 'Python', 'Full Stack'].map((filter) => (
              <button
                key={filter}
                onClick={() => setActiveFilter(filter)}
                className={`px-6 py-2 rounded-lg font-semibold transition-all duration-300 transform hover:scale-105 ${
                  activeFilter === filter
                    ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg shadow-blue-500/25'
                    : 'bg-white/10 text-gray-300 hover:bg-white/20 border border-white/20 hover:border-blue-500/50'
                }`}
              >
                {filter}
              </button>
            ))}
          </div>

          {/* Projects Grid */}
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {filteredProjects.map((project, index) => (
              <div key={project.id} className={`group bg-gradient-to-br from-white/10 to-white/5 rounded-lg overflow-hidden hover:transform hover:scale-105 transition-all duration-500 border border-white/10 hover:border-blue-500/50 hover:shadow-2xl hover:shadow-blue-500/25 card-hover scroll-reveal`} style={{ animationDelay: `${index * 0.2}s` }}>
                <div className="relative overflow-hidden">
                  <img src={project.images[0]} alt={project.title} className="w-full h-48 object-cover group-hover:scale-110 transition-transform duration-500" />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                  {/* Image Gallery Button */}
                  <div className="absolute top-4 right-4 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                    <button
                      onClick={() => {
                        setSelectedProject(project);
                        setCurrentImageIndex(0);
                      }}
                      className="bg-black/70 hover:bg-black/90 text-white p-2 rounded-lg transition-all duration-300 transform hover:scale-110"
                    >
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                      </svg>
                    </button>
                  </div>
                  {/* Image Counter */}
                  <div className="absolute bottom-4 right-4 bg-black/70 text-white px-2 py-1 rounded text-sm opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                    {project.images.length} images
                  </div>
                </div>
                <div className="p-6">
                  <h3 className="text-xl font-bold text-white mb-2 group-hover:text-blue-400 transition-colors duration-300">{project.title}</h3>
                  <p className="text-gray-300 mb-4 leading-relaxed">{project.description}</p>
                  <div className="flex flex-wrap gap-2 mb-4">
                    {project.technologies.map((tech, techIndex) => (
                      <span key={techIndex} className="px-3 py-1 bg-gradient-to-r from-blue-600 to-purple-600 text-white text-sm rounded-full hover:scale-105 transition-transform duration-200">
                        {tech}
                      </span>
                    ))}
                  </div>
                  <div className="flex gap-4">
                    {project.liveDemo && project.liveDemo !== '#' ? (
                      <a href={project.liveDemo} target="_blank" rel="noopener noreferrer" className="group/btn bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white px-4 py-2 rounded-lg transition-all duration-300 transform hover:scale-105 hover:shadow-lg hover:shadow-blue-500/25">
                        <span className="flex items-center">
                          Live Demo
                          <svg className="ml-1 w-4 h-4 group-hover/btn:translate-x-1 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                          </svg>
                        </span>
                      </a>
                    ) : (
                      <a href="#contact" className="group/btn bg-gradient-to-r from-blue-600 to-purple-600 text-white px-4 py-2 rounded-lg transition-all duration-300 transform hover:scale-105 hover:shadow-lg hover:shadow-blue-500/25">
                        <span className="flex items-center">
                          Request Demo
                          <svg className="ml-1 w-4 h-4 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
                          </svg>
                        </span>
                      </a>
                    )}
                    {project.github && project.github !== '#' ? (
                      <a href={project.github} target="_blank" rel="noopener noreferrer" className="group/btn bg-transparent border border-white text-white px-4 py-2 rounded-lg hover:bg-white hover:text-gray-900 transition-all duration-300 transform hover:scale-105">
                        <span className="flex items-center">
                          GitHub
                          <svg className="ml-1 w-4 h-4 group-hover/btn:rotate-12 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 20l4-16m4 4l4 4-4 4M6 16l-4-4 4-4" />
                          </svg>
                        </span>
                      </a>
                    ) : (
                      <div className="bg-transparent border border-white/40 text-white/70 px-4 py-2 rounded-lg cursor-not-allowed opacity-60">
                        <span className="flex items-center">
                          Private Repository
                          <svg className="ml-1 w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 11c1.657 0 3-1.343 3-3S13.657 5 12 5 9 6.343 9 8s1.343 3 3 3z M5.121 17.804A9 9 0 1118.88 17.804L12 21l-6.879-3.196z" />
                          </svg>
                        </span>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Image Gallery Modal */}
          {selectedProject && (
            <div className="fixed inset-0 bg-black/90 z-50 flex items-center justify-center p-4">
              <div className="relative max-w-4xl w-full">
                {/* Close Button */}
                <button
                  onClick={() => {
                    setSelectedProject(null);
                    setCurrentImageIndex(0);
                  }}
                  className="absolute top-4 right-4 z-10 bg-black/70 hover:bg-black/90 text-white p-3 rounded-full transition-all duration-300 transform hover:scale-110"
                >
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>

                {/* Main Image */}
                <div className="relative">
                  <img
                    src={selectedProject.images[currentImageIndex]}
                    alt={`${selectedProject.title} - Image ${currentImageIndex + 1}`}
                    className="w-full h-auto max-h-[70vh] object-contain rounded-lg"
                  />

                  {/* Navigation Arrows */}
                  {selectedProject.images.length > 1 && (
                    <>
                      <button
                        onClick={() => setCurrentImageIndex((prev) => prev === 0 ? selectedProject.images.length - 1 : prev - 1)}
                        className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-black/70 hover:bg-black/90 text-white p-3 rounded-full transition-all duration-300 transform hover:scale-110"
                      >
                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                        </svg>
                      </button>
                      <button
                        onClick={() => setCurrentImageIndex((prev) => prev === selectedProject.images.length - 1 ? 0 : prev + 1)}
                        className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-black/70 hover:bg-black/90 text-white p-3 rounded-full transition-all duration-300 transform hover:scale-110"
                      >
                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                        </svg>
                      </button>
                    </>
                  )}
                </div>

                {/* Image Counter */}
                <div className="text-center mt-4 text-white">
                  <span className="bg-black/70 px-3 py-1 rounded-full text-sm">
                    {currentImageIndex + 1} / {selectedProject.images.length}
                  </span>
                </div>

                {/* Thumbnail Navigation */}
                {selectedProject.images.length > 1 && (
                  <div className="flex justify-center gap-2 mt-4">
                    {selectedProject.images.map((image, index) => (
                      <button
                        key={index}
                        onClick={() => setCurrentImageIndex(index)}
                        className={`w-16 h-16 rounded-lg overflow-hidden transition-all duration-300 transform hover:scale-110 ${
                          currentImageIndex === index ? 'ring-2 ring-blue-400' : 'ring-1 ring-white/30'
                        }`}
                      >
                        <img
                          src={image}
                          alt={`Thumbnail ${index + 1}`}
                          className="w-full h-full object-cover"
                        />
                      </button>
                    ))}
                  </div>
                )}

                {/* Project Info */}
                <div className="text-center mt-6">
                  <h3 className="text-2xl font-bold text-white mb-2">{selectedProject.title}</h3>
                  <p className="text-gray-300 max-w-2xl mx-auto">{selectedProject.description}</p>
                </div>
              </div>
            </div>
          )}
        </div>
      </section>

      {isResumeOpen && (
        <div className="fixed inset-0 bg-black/80 z-50 flex items-center justify-center p-4">
          <div className="bg-gray-900 text-white rounded-lg max-w-3xl w-full overflow-hidden border border-white/10">
            <div className="flex items-center justify-between px-4 py-3 border-b border-white/10">
              <div className="font-semibold">Resume Preview</div>
              <button onClick={() => setIsResumeOpen(false)} className="text-gray-300 hover:text-white">✕</button>
            </div>
            <div className="p-4 bg-black/30">
              <iframe title="resume" src="/resume.pdf" className="w-full h-[60vh] rounded"></iframe>
            </div>
            <div className="flex items-center justify-between px-4 py-3 border-t border-white/10">
              <div className="text-sm text-gray-300">
                Views: <span className="text-blue-400 font-semibold">{resumeViews}</span> · Downloads: <span className="text-blue-400 font-semibold">{resumeDownloads}</span>
              </div>
              <div className="flex gap-2">
                <a href="/resume.pdf" download onClick={() => setResumeDownloads(d => d + 1)} className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-4 py-2 rounded-lg hover:from-blue-700 hover:to-purple-700">Download PDF</a>
                <button onClick={() => setIsResumeOpen(false)} className="border border-white/20 px-4 py-2 rounded-lg hover:bg-white/10">Close</button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Contact Section */}
      <section id="contact" className="py-16 px-4 bg-black/20 relative">
        <div className="max-w-4xl mx-auto">
          <h2 className="text-4xl font-bold text-white text-center mb-12 bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent scroll-reveal">
            Get In Touch
          </h2>
          <div className="grid md:grid-cols-2 gap-12">
            <div>
              <h3 className="text-2xl font-bold text-white mb-6">Contact Information</h3>
              <div className="space-y-4">
                <div className="flex items-center text-gray-300 group hover:text-blue-400 transition-colors duration-300">
                  <span className="mr-3 text-xl">📧</span>
                  <span>hellodanish97@gmail.com</span>
                </div>
                <div className="flex items-center text-gray-300 group hover:text-blue-400 transition-colors duration-300">
                  <span className="mr-3 text-xl">📱</span>
                  <span>+91 8420119949</span>
                </div>
                <div className="flex items-center text-gray-300 group hover:text-blue-400 transition-colors duration-300">
                  <span className="mr-3 text-xl">📍</span>
                  <span>Kolkata, West Bengal, India</span>
                </div>
              </div>

              {/* Social Links */}
              <div className="mt-8">
                <h4 className="text-white font-semibold mb-4">Professional Links</h4>
                <ul className="space-y-3">
                  <li className="flex items-center gap-3">
                    <a
                      href="https://github.com/Hellodanish9"
                      title="github.com/Hellodanish9"
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center gap-3 text-gray-300 hover:text-blue-400 transition-colors duration-300"
                      aria-label="GitHub Profile"
                    >
                      <Github className="w-5 h-5 text-blue-400" />
                      <span className="font-medium">GitHub</span>
                      <span className="text-gray-400">@Hellodanish9</span>
                    </a>
                    <button
                      onClick={() => copyHandle('gh', '@Hellodanish9')}
                      className="text-xs px-2 py-1 border border-white/20 rounded text-gray-300 hover:bg-white/10 transition-colors"
                      aria-label="Copy GitHub handle"
                    >
                      {copied.gh ? 'Copied' : 'Copy'}
                    </button>
                  </li>
                  <li className="flex items-center gap-3">
                    <a
                      href="https://www.linkedin.com/in/danish-khan-657502252/"
                      title="linkedin.com/in/danish-khan-657502252/"
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center gap-3 text-gray-300 hover:text-blue-400 transition-colors duration-300"
                      aria-label="LinkedIn Profile"
                    >
                      <Linkedin className="w-5 h-5 text-blue-400" />
                      <span className="font-medium">LinkedIn</span>
                      <span className="text-gray-400">@danish-khan-657502252</span>
                    </a>
                    <button
                      onClick={() => copyHandle('li', '@danish-khan-657502252')}
                      className="text-xs px-2 py-1 border border-white/20 rounded text-gray-300 hover:bg-white/10 transition-colors"
                      aria-label="Copy LinkedIn handle"
                    >
                      {copied.li ? 'Copied' : 'Copy'}
                    </button>
                  </li>
                  <li>
                    <button
                      onClick={() => { setIsResumeOpen(true); setResumeViews(v => v + 1); }}
                      className="flex items-center gap-3 text-gray-300 hover:text-blue-400 transition-colors duration-300"
                      aria-label="Resume or CV"
                    >
                      <FileText className="w-5 h-5 text-blue-400" />
                      <span className="font-medium">Resume/CV</span>
                      <span className="text-gray-400">Preview & Download</span>
                    </button>
                  </li>
                </ul>
              </div>
            </div>
            <div>
              <form className="space-y-4" onSubmit={handleContactSubmit} noValidate>
                <input
                  type="text"
                  name="name"
                  value={contactForm.name}
                  onChange={handleContactChange}
                  placeholder="Your Name"
                  required
                  className="w-full p-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-500/20 transition-all duration-300"
                />
                <input
                  type="email"
                  name="email"
                  value={contactForm.email}
                  onChange={handleContactChange}
                  placeholder="Your Email"
                  required
                  className="w-full p-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-500/20 transition-all duration-300"
                />
                <textarea
                  name="message"
                  value={contactForm.message}
                  onChange={handleContactChange}
                  placeholder="Your Message"
                  rows="4"
                  required
                  className="w-full p-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-500/20 transition-all duration-300"
                ></textarea>
                <button
                  type="submit"
                  disabled={isSubmitting}
                  className={`w-full bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white font-bold py-3 px-6 rounded-lg transition-all duration-300 transform hover:scale-105 hover:shadow-lg hover:shadow-blue-500/25 ${isSubmitting ? 'opacity-70 cursor-not-allowed' : ''}`}
                >
                  {isSubmitting ? 'Sending...' : 'Send Message'}
                </button>
                {submitStatus === 'success' && (
                  <div className="text-green-400 text-sm">Thanks! Your message has been sent.</div>
                )}
                {submitStatus === 'error' && (
                  <div className="text-red-400 text-sm">Oops! Something went wrong. Please try again.</div>
                )}
              </form>
            </div>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-black/40 py-8 px-4 border-t border-white/10 relative">
        <div className="max-w-7xl mx-auto text-center">
          <p className="text-gray-400">© 2025 Danish Khan. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
